import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function UserProfileMatches() {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState(null);
  const { id } = useParams(); // This is the username

  // Fetch user ID from username
  const fetchUserId = async (username) => {
    try {
      const response = await axios.get(`http://localhost:8000/api/profiles/`);
      const profiles = response.data;
      const userProfile = profiles.find(profile => profile.user === username);
      return userProfile ? userProfile.user_id : null;
    } catch (error) {
      console.error('Error fetching user ID:', error);
      return null;
    }
  };

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const userId = await fetchUserId(id);
        if (userId) {
          const response = await axios.get(`http://localhost:8000/api/matchmaking/${userId}/`);
          setMatches(response.data);
        } else {
          setError('User ID not found');
        }
      } catch (error) {
        setError('Error fetching matches');
        console.error('Error fetching matches:', error);
      }
    };

    fetchMatches();
  }, [id]);

  return (
    <div>
      <h1>Matches for User {id}</h1>
      {error && <p>{error}</p>}
      <ul>
        {matches.length > 0 ? (
          matches.map(profile => (
            <li key={profile.user}>
              {profile.user} - {profile.location}
            </li>
          ))
        ) : (
          <p>No matches found</p>
        )}
      </ul>
    </div>
  );
}

export default UserProfileMatches;
